import EmployeeSingle from './EmployeeSingle';
import App from '../App';

class Employee extends Component {
    constructor(props) {
        super(props);
        this.state = {
            employee:[],
        };
    }

    componentDidMount(){
        const url = 'https://randomuser.me/api/?results=15';
         fetch(url)
            .then((response) => {
                 return response.json();
        })
        .then((data) => {
            this.setState({employee: data.results})
        })
        .catch((error)=> console.log(error));
    }

    renderItems() {
        return this.state.employee.map((item) => (
            <EmployeeSingle key={item.id} item={item}/>
        ));
    }

