     search:""
 }   


componentDidMount() {
    API.getEmployees()
    .then(res => {
         this.setState({results: res.data.results})
         console.log(this.state.results)
    }).catch(err =>console.log(err))
}


render() {
    return(
        <div>
        <table class="table-auto">
  <thead>
