        this.state = {
                query:'',
                results:{},
                loading:false,
                message:''
        }

 }


handleInput = (event) => {
        const query = event.target.value;
};
        render() {
                const {query} = this.state;
        return(
        <div>
        <div className="container">
                <h2>Search for Employee</h2>
                <input 
                        type= "text"
                        name="query"
                        value={query}
                        id="search-input"
                        placeholder="Search..."
                />
                </div>
        </div>
        )}
}
